INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('American', 'American__Civ6_.png', 'All  Diplomatic policy slots in the current government are converted to  Wildcard slots. +1  Diplomatic Favor per turn for each Wildcard slot in the current government.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Arabian', 'Arabian__Civ6_.png', 'Automatically receives the final  Great Prophet when the next-to-last one is claimed (unless one has already been earned through other means). +1  Science per foreign city following Arabias Religion.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Australian', 'Australian__Civ6_.png', '+3  Housing in coastal cities. Building a Pasture triggers a Culture Bomb, claiming surrounding tiles. Campus, Commercial Hub, Holy Site, and Theater Square districts gain +1 to their yields in tiles with Charming Appeal, and +3 in tiles with Breathtaking Appeal.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Aztec', 'Aztec__Civ6_.png', 'Can spend Builder charges to complete 20% of a districts  Production cost.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Babylonian', 'Babylonian__Civ6_.png', 'Eurekas instantly unlock their respective technologies. -50%  Science per turn.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Brazilian', 'Brazilian__Civ6_.png', 'Rainforest tiles provide a +1 adjacency bonus for Campus, Commercial Hub, Holy Site, and Theater Square districts. Owned Rainforests provide +1 Appeal to adjacent tiles, instead of the usual -1.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Byzantine', 'Byzantine__Civ6_.png', '+3  Combat and  Religious Strength for all units for each Holy City converted to Byzantiums Religion (including Byzantiums own Holy City). Byzantiums Religion is spread to nearby cities when a unit from an enemy civilization or city-state is defeated. +1  Great Prophet point from Holy Sites.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Canadian', 'Canadian__Civ6_.png', 'Cannot declare Surprise Wars or war on city-states. Surprise Wars cannot be declared on Canada. For every 100  Tourism earned, gain 1  Diplomatic Favor. +100%  Diplomatic Favor gained from successfully completing Emergencies or Scored Competitions.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Chinese', 'Chinese__Civ6_.png', 'Eurekas and  Inspirations provide an extra 10% of the  Science and  Culture cost for researching technologies and civics. Completing a Wonder grants a  Eureka and  Inspiration from that Wonders era.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Cree', 'Cree__Civ6_.png', 'Gains +1  Trade Route capacity and a free Trader with Pottery. Unclaimed tiles within three tiles of a Cree city come under Cree control when a Trader first moves into them.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Dutch', 'Dutch__Civ6_.png', 'Rivers provide a +2 adjacency bonus for Campus, Theater Square, and Industrial Zone districts. Building a Harbor triggers a Culture Bomb, claiming surrounding tiles. +50%  Production towards the Dam district and Flood Barrier building.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Egyptian', 'Egyptian__Civ6_.png', '+15%  Production towards districts and wonders built next to a River. Districts, improvements and units are immune to damage from floods.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('English', 'English__Civ6_.png', 'Iron and Coal Mines accumulate +2 resources per turn. +100%  Production towards Military Engineers. Military Engineers receive +2 charges. Buildings that provide additional yields when powered receive +4 of their respective yields. +20%  Production towards Industrial Zone buildings. Harbor buildings grant +10 Strategic Resource stockpiles.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Ethiopian', 'Ethiopian__Civ6_.png', 'Improved resource tiles receive +1  Faith for each copy of the resource the city owns. International  Trade Routes grant +5  Faith for each resource in the origin city. Can purchase Archaeologists and Archaeological Museums with Faith.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('French', 'French__Civ6_.png', '+20%  Production towards Medieval, Renaissance and Industrial Era Wonders. Double  Tourism from Wonders of any Era.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Gallic', 'Gallic__Civ6_.png', 'Mines gain +1  Culture, provide a +5 adjacency bonus for all districts and trigger a Culture Bomb when built, claiming surrounding unowned tiles. Specialty districts do not receive an adjacency bonus from other districts and cannot be built next to the City Center.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Georgian', 'Georgian__Civ6_.png', 'Dedications chosen at the beginning of a Golden Age or Heroic Age also grant their Normal Age bonuses towards improving Era Score, in addition to their regular bonuses. +50%  Production towards walls.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('German', 'German__Civ6_.png', 'Each city can build one more specialty  District than the  Population limit would normally allow.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Gran Colombian', 'Gran_Colombian__Civ6_.png', '+1  Movement for all units. Promoting a unit does not end that units turn.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Greek', 'Greek__Civ6_.png', 'Gains an additional  Wildcard policy slot in all Governments.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Hungarian', 'Hungarian__Civ6_.png', '+50%  Production for districts and buildings built across a river from a City Center.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Incan', 'Incan__Civ6_.png', 'Citizens may work Mountain tiles. Mountain tiles provide +2  Production, and +1  Food for each adjacent Terrace Farm. Mountain tiles provide an additional +1  Production when Inca reaches the Industrial Era.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Indian', 'Indian__Civ6_.png', 'Indian cities receive the Follower Beliefs of all religions with at least one follower in them, not just the majority religion, and gain +1  Amenity for each religion with at least one follower in them. +2 Spread Religion charges for Missionaries. +100% Religious pressure from Indian  Trade Routes.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Indonesian', 'Indonesian__Civ6_.png', 'Coast and Lake tiles provide a +5 adjacency bonus for Holy Site, Campus, Industrial Zone, and Theater Square districts. Entertainment Complexes built adjacent to a Coast or Lake tile provide +1  Amenity.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Japanese', 'Japanese__Civ6_.png', 'Districts receive a +1 adjacency bonus for each adjacent district, instead of +5.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Khmer', 'Khmer__Civ6_.png', 'Cities with an Aqueduct receive +1  Amenity, and +1  Faith for every  Population. Farms receive +2  Food when adjacent to an Aqueduct, and +1  Faith when adjacent to a Holy Site.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Kongolese', 'Kongolese__Civ6_.png', 'Relics,  Artifacts and  Sculptures grant +2  Food, +2  Production, +1  Faith, and +4  Gold. The Palace has four extra slots for Great Works. +50%  Great Artist,  Great Musician, and  Great Merchant points gained from all sources.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Korean', 'Korean__Civ6_.png', 'Farms receive +1  Food and Mines receive +1  Science for each adjacent Seowon.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Macedonian', 'Macedonian__Civ6_.png', 'Conquering a city grants a free  Eureka for each Encampment and Campus district in the conquered city and a free  Inspiration for each Holy Site and Theater Square district.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Malian', 'Malian__Civ6_.png', 'City Centers receive +1  Faith and +1  Food for every adjacent Desert and Desert Hills tile. Mines receive -1  Production and +4  Gold. May purchase Commercial Hub buildings with  Faith. -30%  Production towards buildings and units.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Māori', 'Maori__Civ6_.png', 'Starts with Sailing and Shipbuilding unlocked and the ability to enter Ocean tiles. +5  Combat Strength and +2  Movement for embarked units. Unimproved Woods and Rainforests grant +1  Production, increasing to +2  Production with Mercantilism and +3  Production with Conservation. +1  Food from Fishing Boats. Building a Fishing Boat triggers a Culture Bomb, claiming surrounding tiles. Resources cannot be harvested.  Great Writers cannot be earned.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Mapuche', 'Mapuche__Civ6_.png', 'Cities with an established Governor gain +5%  Culture, +5%  Production, and +10% combat experience towards all units trained in the city. These numbers are tripled in cities not founded by the Mapuche. All cities within 9 tiles of a city with your Governor gain +4 Loyalty per turn towards your civilization.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Mayan', 'Mayan__Civ6_.png', 'Cities do not receive additional  Housing from being adjacent to fresh water or coast. Cities gain +1  Amenity for each Luxury Resource adjacent to the City Center. Farms grant +1  Housing, +1  Production if adjacent to an Observatory, and +1  Gold.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Mongolian', 'Mongolian__Civ6_.png', 'Sending a  Trade Route immediately creates a  Trading Post in the destination city, instead of when the  Trade Route is completed. Gains an extra level of  Diplomatic Visibility with civilizations that have a Mongolian  Trading Post. +6  Combat Strength for all units for each level of  Diplomatic Visibility Mongolia has over the other civilization, instead of the usual +3  Combat Strength.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Norwegian', 'Norwegian__Civ6_.png', 'Norwegian units gain the ability to enter Ocean tiles with Shipbuilding, instead of Cartography, and pay no additional  Movement costs to embark or disembark. Naval melee units can heal in neutral territory.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Nubian', 'Nubian__Civ6_.png', '+30%  Production toward Ranged units. Ranged units gain +50% combat experience. +1  Production for Mines over strategic resources, and +2  Gold for Mines over bonus and luxury resources.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Ottoman', 'Ottoman__Civ6_.png', '+30%  Production toward Siege units. Siege units gain +5  Combat Strength when attacking defensible districts. Conquering a city doesnt cause that city to lose  Population. +1  Amenity and +4 Loyalty per turn for cities not founded by the Ottomans.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Persian', 'Persian__Civ6_.png', 'Gains +1  Trade Route capacity with Political Philosophy. Domestic  Trade Routes provide +2  Gold and +1  Culture. Roads built inside Persian territory are one level more advanced than usual.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Phoenician', 'Phoenician__Civ6_.png', 'Starts with the  Eureka for Writing. Coastal cities founded by Phoenicia and in the same continent as their  Capital always have full Loyalty. Settlers receive +2  Movement and  Sight while embarked, and pay no  Movement costs to embark or disembark.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Polish', 'Polish__Civ6_.png', 'Building an Encampment or Fort triggers a Culture Bomb, claiming surrounding tiles. One  Military policy slot in all governments is converted into a  Wildcard policy slot.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Portuguese', 'Portuguese__Civ6_.png', 'International  Trade Routes can only be sent to cities on the coast or with a Harbor, but gain a +50% increase to all yields. Traders have +50% range over water, and can embark as soon as they are unlocked.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Roman', 'Roman__Civ6_.png', 'Founded or conquered cities start with a Trading Post and, if within  Trade Route range of the  Capital, a road to it.  Trade Routes generate +1 additional  Gold from Roman Trading Posts they pass through.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Russian', 'Russian__Civ6_.png', 'Founded cities start with five additional tiles. Tundra tiles provide +1  Faith and +1  Production, in addition to their usual yields. Units are immune to damage from Blizzards. +100% damage from Blizzards inside Russian territory to civilizations at war with Russia.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Scottish', 'Scottish__Civ6_.png', 'Happy cities gain +5%  Science and +5%  Production, and generate +1  Great Scientist point in their Campuses and +1  Great Engineer point in their Industrial Zones. Ecstatic cities double these bonuses.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Scythian', 'Scythian__Civ6_.png', 'Building a light cavalry unit or Saka Horse Archer grants a free second copy of that unit.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Spanish', 'Spanish__Civ6_.png', 'May form Fleets and Armadas with Mercantilism, instead of Nationalism and Mobilization.  Trade Routes receive 3  Gold, 2  Faith and 1  Production. Trade Routes between cities on different continents gain triple these yields. Cities not on your original  Capitals continent receive 25% extra  Production towards  Districts and a Builder when founded.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Sumerian', 'Sumerian__Civ6_.png', 'Capturing a Barbarian Outpost also grants a Tribal Village reward. Levying city-state units costs 50% less  Gold.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Swedish', 'Swedish__Vikings_Traders_and_Raiders___Civ6_.png', 'Gains 50  Diplomatic Favor whenever a Great Person is earned. +1  Great Engineer points from Factories and +1  Great Scientist points from Universities. Swedens presence in the game adds three unique World Congress competitions in the Industrial Era.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Vietnamese', 'Vietnamese__Civ6_.png', 'Land specialty  Districts can only be built on Woods, Rainforest or Marsh tiles. Buildings on these features receive additional yields: +1  Culture in Woods, +1  Science in Rainforest, and +1  Production in Marsh. Can plant Woods with Medieval Faires, instead of Conservation.');
INSERT INTO civilizations (name, image_key, civilization_bonus) VALUES ('Zulu', 'Zulu__Civ6_.png', 'Capturing a city will upgrade the capturing unit, as long as the necessary civics are unlocked: from a normal unit into a Corps or Fleet, and from a Corps or Fleet into an Army or Armada. Cities with a garrisoned unit gain +3 Loyalty per turn, increasing to +5 Loyalty if the garrisoned unit is a Corps or Army.');
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Abraham Lincoln', c.id, 'Abraham_Lincoln__Civ6_.png', 'Industrial Zones grant +2  Amenities and +3 Loyalty per turn but Plantations give -2 Loyalty. Receives a free Melee unit after constructing Industrial Zones and their buildings. The free unit does not require resources when created or to maintain and receives +5  Combat Strength.' FROM civilizations c WHERE c.name = 'American';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Alexander', c.id, 'Alexander__Civ6_.png', 'Macedonian cities never incur war weariness. All military units heal completely when a city with a Wonder is captured. Gains the Hetairoi unique unit with Horseback Riding.' FROM civilizations c WHERE c.name = 'Macedonian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Amanitore', c.id, 'Amanitore__Civ6_.png', '+20%  Production towards  Districts, increasing to +40%  Production if there is a Nubian Pyramid adjacent to the City Center.' FROM civilizations c WHERE c.name = 'Nubian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Ambiorix', c.id, 'Ambiorix__Civ6_.png', 'Receives  Culture equal to 20% of the  Production cost after training a non-civilian unit. Melee, ranged and anti-cavalry units receive +2  Combat Strength for each adjacent military unit.' FROM civilizations c WHERE c.name = 'Gallic';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Bà Triệu', c.id, 'B__Triệu__Civ6_.png', 'All units gain +5  Combat Strength when fighting in Rainforest, Marsh, and Woods tiles, and +1  Movement if they start their turn there. Both of these bonuses are doubled in tiles inside Vietnamese territory.' FROM civilizations c WHERE c.name = 'Vietnamese';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Basil II', c.id, 'Basil_II__Civ6_.png', 'Heavy and light cavalry units deal full damage when attacking cities following the same Religion as Byzantium. Gains the Tagma unique unit with Divine Right.' FROM civilizations c WHERE c.name = 'Byzantine';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Catherine de Medici (Black Queen)', c.id, 'Catherine_de_Medici__Civ6_.png', 'Gains +1 level of  Diplomatic Visibility with every encountered civilization. Receives a free Spy with Castles. All Spies start with a free promotion.' FROM civilizations c WHERE c.name = 'French';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Catherine de Medici (Magnificence)', c.id, 'Catherine_de_Medici__Magnificence___Civ6_.png', '+2  Culture for improved Luxury resources adjacent to a Theater Square or Château. Cities with a Theater Square gain the unique Court Festival project, which upon completion grants  Culture and  Tourism based on the number of excess copies of the Luxury resources that France possesses.' FROM civilizations c WHERE c.name = 'French';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Chandragupta', c.id, 'Chandragupta__Civ6_.png', 'Can declare a War of Territorial Expansion with Military Training, instead of Mobilization. +2  Movement and +5  Combat Strength for all units for the next 10 turns after declaring a War of Territorial Expansion.' FROM civilizations c WHERE c.name = 'Indian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Cleopatra (Egyptian)', c.id, 'Cleopatra__Civ6_.png', 'International  Trade Routes grant +4  Gold.  Trade Routes sent to Egypt from other civilizations provide +2  Food for them and +2  Gold for Egypt. +100% Alliance Points from trading with allies.' FROM civilizations c WHERE c.name = 'Egyptian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Cleopatra (Ptolemaic)', c.id, 'Cleopatra__Ptolemaic___Civ6_.png', '+1  Food and  Culture for resources on Floodplains. Owned Floodplains grant +1 Appeal to adjacent tiles, instead of the usual -1.' FROM civilizations c WHERE c.name = 'Egyptian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Cyrus', c.id, 'Cyrus__Civ6_.png', '+2  Movement for all units for the next 10 turns after declaring a Surprise War. Declaring a Surprise War only counts as a Formal War for the purpose of  Grievances and war weariness. Occupied cities have no penalties to their yields. +5 Loyalty per turn in occupied cities with a garrisoned unit.' FROM civilizations c WHERE c.name = 'Persian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Dido', c.id, 'Dido__Civ6_.png', 'Cities with a Cothon gain the unique Move Capital project, which moves the  Capital to that city. Gains +1  Trade Route capacity after building the Government Plaza or any Government Plaza building. +50%  Production towards  Districts in the city with the Government Plaza.' FROM civilizations c WHERE c.name = 'Phoenician';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Eleanor of Aquitaine (English)', c.id, 'Eleanor_of_Aquitaine__English___Civ6_.png', 'Each Great Work in her cities causes foreign cities within 9 tiles to lose 1 Loyalty per turn. Any foreign city that leaves its civilization due to loss of Loyalty and is receiving the most Loyalty pressure from Eleanor instantly joins her empire, instead of becoming a Free City.' FROM civilizations c WHERE c.name = 'English';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Eleanor of Aquitaine (French)', c.id, 'Eleanor_of_Aquitaine__French___Civ6_.png', 'Grand Tour +20% Production towards Medieval, Renaissance and Industrial Era Wonders. Double Tourism from Wonders of any Era.' FROM civilizations c WHERE c.name = 'French';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Elizabeth I', c.id, 'Elizabeth_I__Civ6_.png', '+2  Trade Route capacity upon recruiting the first  Great Admiral.  Trade Routes to city-states gain +3  Gold for every specialty district at the origin city. +100% yields from plundering  Trade Routes.' FROM civilizations c WHERE c.name = 'English';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Frederick Barbarossa', c.id, 'Frederick_Barbarossa__Civ6_.png', 'Gains an additional  Military policy slot in all Governments. +7  Combat Strength for all units when fighting city-states and their units.' FROM civilizations c WHERE c.name = 'German';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Gandhi', c.id, 'Gandhi__Civ6_.png', '+5  Faith for each met civilization (including India) that has founded a Religion and is not at war. Enemies receive double war weariness from fighting against Gandhi.' FROM civilizations c WHERE c.name = 'Indian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Genghis Khan', c.id, 'Genghis_Khan__Civ6_.png', 'Cavalry class units gain +3  Combat Strength and have a chance to capture defeated cavalry class enemy units.' FROM civilizations c WHERE c.name = 'Mongolian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Gilgamesh', c.id, 'Gilgamesh__Civ6_.png', '25%  Production increase when claiming Heroes, and Heroes have 20% more  Lifespan.' FROM civilizations c WHERE c.name = 'Sumerian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Gitarja', c.id, 'Gitarja__Civ6_.png', 'May purchase naval units with  Faith. Religious units pay no movement cost to embark or disembark. City Centers adjacent to Coast or Lake tiles gain +2  Faith.' FROM civilizations c WHERE c.name = 'Indonesian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Gorgo', c.id, 'Gorgo__Civ6_.png', 'Killing a unit provides  Culture equal to 50% of its  Combat Strength. All units gain +1  Combat Strength for every active Military Policy card in the current government.' FROM civilizations c WHERE c.name = 'Greek';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Hammurabi', c.id, 'Hammurabi__Civ6_.png', 'Upon building each type of specialty  District, except the Government Plaza, for the first time, instantly receives the building with the lowest  Production cost that can be built in that district. Upon building any other district for the first time, receives a free  Envoy.' FROM civilizations c WHERE c.name = 'Babylonian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Harald Hardrada (Konge)', c.id, 'Harald_Hardrada__Civ6_.png', '+50%  Production toward naval melee units. All naval melee units gain the ability to perform Coastal Raids. Receive  Science in addition to  Gold when pillaging and coastal raiding Mines and  Culture in addition to  Faith when pillaging and coastal raiding Quarries, Pastures, Plantations, and Camps. Gains the Viking Longship unique unit with Sailing.' FROM civilizations c WHERE c.name = 'Norwegian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Harald Hardrada (Varangian)', c.id, 'Harald_Hardrada__Varangian___Civ6_.png', 'Levying city-state units costs 75% less  Gold. All units pay 2 less maintenance. Killing a unit with a levied unit grants  Faith,  Culture, and  Science equal to 50% of the defeated units  Combat Strength. +1  Influence point per turn from each Stave Church.' FROM civilizations c WHERE c.name = 'Norwegian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Harald Hardrada', c.id, 'Harald_Hardrada__Civ6_.png', 'Viking Units ignore additional Movement costs from embarking and disembarking.' FROM civilizations c WHERE c.name = 'Norwegian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Hojo Tokimune', c.id, 'Hojo_Tokimune__Civ6_.png', '+5  Combat Strength for land units and naval units fighting on coastal and shallow water tiles. Encampment, Holy Site and Theater Square districts are built in half the time. Districts, improvements and units are immune to damage from Hurricanes. +100% damage from Hurricanes in Japanese territory to civilizations at war with Japan.' FROM civilizations c WHERE c.name = 'Japanese';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Jadwiga', c.id, 'Jadwiga__Civ6_.png', 'Taking territory from a foreign city with a Culture Bomb converts it to Polands religion.  Relics grant +4  Gold, +2  Culture and +2  Faith. Holy Sites receive a +1 adjacency bonus from districts, instead of +5.' FROM civilizations c WHERE c.name = 'Polish';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Jayavarman VII', c.id, 'Jayavarman_VII__Civ6_.png', 'Holy Sites grant  Food equal to their adjacency bonus, receive a +2 adjacency bonus from Rivers, grant 2  Housing if built next to a River, and trigger a Culture Bomb when built, claiming surrounding tiles.' FROM civilizations c WHERE c.name = 'Khmer';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'João III', c.id, 'Jo_o_III__Civ6_.png', '+1  Sight for all units. Meeting another civilization grants +1  Trade Route capacity. Gains Open Borders with all city-states.' FROM civilizations c WHERE c.name = 'Portuguese';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'John Curtin', c.id, 'John_Curtin__Civ6_.png', '+100%  Production if they have either received a declaration of war or liberated a city in the past 10 turns.' FROM civilizations c WHERE c.name = 'Australian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Julius Caesar', c.id, 'Julius_Caesar__Civ6_.png', 'Gains 300  Gold after conquering a city for the first time, and after earning  Gold from a Barbarian Outpost. Both amounts become 500  Gold after researching Metal Casting and 700  Gold after Steel (on Standard speed). Units gain 5  Combat Strength and receive full experience when fighting Barbarians.' FROM civilizations c WHERE c.name = 'Roman';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Kristina', c.id, 'Kristina__Civ6_.png', 'Buildings with at least three Great Work slots and wonders with at least two Great Work slots are automatically themed when all slots are filled. Gains the Queens Bibliotheque unique building in the Government Plaza.' FROM civilizations c WHERE c.name = 'Swedish';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Kublai Khan (Chinese)', c.id, 'Kublai_Khan__Chinese___Civ6_.png', 'Dynastic Cycle Eureka s and Inspiration s provide an extra 10% of the Science and Culture cost for researching technologies and civics. Completing a Wonder grants a Eureka and Inspiration from that Wonders era.' FROM civilizations c WHERE c.name = 'Chinese';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Kublai Khan (Mongolian)', c.id, 'Kublai_Khan__Mongolian___Civ6_.png', 'Örtöö Sending a Trade Route immediately creates a Trading Post in the destination city, instead of when the Trade Route is completed. Gains an extra level of Diplomatic Visibility with civilizations that have a Mongolian Trading Post. +6 Combat Strength for all units for each level of Diplomatic Visibility Mongolia has over the other civilization, instead of the usual +3 Combat Strength.' FROM civilizations c WHERE c.name = 'Mongolian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Kupe', c.id, 'Kupe__Civ6_.png', 'Starts the game in the Ocean. +2  Science and +2  Culture per turn before the first city is settled. Their first settled city receives a free Builder and +1  Population. The Palace grants +3  Housing and +1  Amenity.' FROM civilizations c WHERE c.name = 'Māori';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Lady Six Sky', c.id, 'Lady_Six_Sky__Civ6_.png', 'Non- Capital cities within 6 tiles of the  Capital gain +10% to all yields and receive a Builder when founded, while cities more than 6 tiles away receive -15% to all yields. +5  Combat Strength for all units within six tiles of the Mayan  Capital.' FROM civilizations c WHERE c.name = 'Mayan';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Lautaro', c.id, 'Lautaro__Civ6_.png', '+10  Combat Strength when fighting civilizations that are in a Golden or Heroic Age, or Free Cities. Defeating an enemy unit within the borders of an enemy city causes that city to lose 20 Loyalty, or 40 Loyalty if that civilization is in a Golden or Heroic Age.' FROM civilizations c WHERE c.name = 'Mapuche';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Ludwig II', c.id, 'Ludwig_II__Civ6_.png', 'Wonders, even unfinished, receive a +2  Culture bonus from each adjacent  District. All  Culture adjacency bonuses provide  Tourism after researching Castles.' FROM civilizations c WHERE c.name = 'German';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Mansa Musa', c.id, 'Mansa_Musa__Civ6_.png', 'International  Trade Routes gain +1  Gold for every flat Desert tile in the sending city. Entering a Golden Age permanently grants +1  Trade Route capacity.' FROM civilizations c WHERE c.name = 'Malian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Matthias Corvinus', c.id, 'Matthias_Corvinus__Civ6_.png', 'Levied city-state units receive +2  Movement and +5  Combat Strength, and can be upgraded at a 75% discount in  Gold or resources. Levying troops from a city-state grants 2  Envoys with that city-state. Gains the Black Army unique unit with Castles.' FROM civilizations c WHERE c.name = 'Hungarian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Menelik II', c.id, 'Menelik_II__Civ6_.png', 'Ethiopian cities founded on Hills receive  Science and  Culture equal to 15% of their  Faith output. +4  Combat Strength for all units when fighting on hills.' FROM civilizations c WHERE c.name = 'Ethiopian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Montezuma', c.id, 'Montezuma__Civ6_.png', 'Improved Luxury resources provide an  Amenity to 2 extra cities. Military units receive +1  Combat Strength for each different improved Luxury resource in Aztec territory.' FROM civilizations c WHERE c.name = 'Aztec';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Mvemba a Nzinga', c.id, 'Mvemba_a_Nzinga__Civ6_.png', 'May not build Holy Sites or found a religion, but receives all the beliefs of any religion that has established itself as Kongos majority religion, not just the Follower beliefs. Building a Mbanza or Theater Square grants a free Apostle of the citys majority religion.' FROM civilizations c WHERE c.name = 'Kongolese';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Nader Shah', c.id, 'Nader_Shah__Civ6_.png', '+5  Combat Strength for all units when attacking full health units. Cities not founded by Nader Shah gain +2  Faith and +3  Gold on domestic  Trade Routes.' FROM civilizations c WHERE c.name = 'Persian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Nzinga Mbande', c.id, 'Nzinga_Mbande__Civ6_.png', 'Cities on the same continent as the  Capital (including the  Capital) receive +10% to all yields, while cities on another continent receive -15% to all yields.' FROM civilizations c WHERE c.name = 'Kongolese';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Pachacuti', c.id, 'Pachacuti__Civ6_.png', 'Domestic  Trade Routes gain +1  Food for every Mountain tile in the origin city. Gains the Qhapaq Ñan unique improvement with Foreign Trade.' FROM civilizations c WHERE c.name = 'Incan';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Pedro II', c.id, 'Pedro_II__Civ6_.png', 'Recruiting or patronizing a  Great Person refunds 20% of their point cost.' FROM civilizations c WHERE c.name = 'Brazilian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Pericles', c.id, 'Pericles__Civ6_.png', '+5%  Culture per city-state Greece has Suzerainty over.' FROM civilizations c WHERE c.name = 'Greek';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Peter', c.id, 'Peter__Civ6_.png', 'Trade Routes to more advanced civilizations grant Russia +1  Science for every three technologies that civilization is ahead of them, and +1  Culture for every three civics.' FROM civilizations c WHERE c.name = 'Russian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Philip II', c.id, 'Philip_II__Civ6_.png', 'Inquisitors can Remove Heresy one extra time and remove 100% of the presence of other religions. Combat and religious units have a bonus of +5  Combat Strength against players following other religions.' FROM civilizations c WHERE c.name = 'Spanish';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Poundmaker', c.id, 'Poundmaker__Civ6_.png', 'All Alliance types provide Shared Visibility.  Trade Routes grant +1  Food in the sending city and +1  Gold in the receiving city per Camp or Pasture in the receiving city.' FROM civilizations c WHERE c.name = 'Cree';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Qin Shi Huang (Mandate of Heaven)', c.id, 'Qin_Shi_Huang__Civ6_.png', 'Builders receive an additional build charge. Can spend Builder charges to complete 15% of the  Production cost for Ancient and Classical wonders. Unlocks Canals with Masonry instead of Steam Power.' FROM civilizations c WHERE c.name = 'Chinese';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Qin Shi Huang (Unifier)', c.id, 'Qin_Shi_Huang__Unifier___Civ6_.png', 'All land melee units gain access to the unique Convert Barbarians action, which consumes the unit to transform all adjacent Barbarian units into Chinese units.' FROM civilizations c WHERE c.name = 'Chinese';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Ramses II', c.id, 'Ramses_II__Civ6_.png', 'Receives  Culture equal to 15% of the  Production cost after completing a building, and 30% after completing a wonder.' FROM civilizations c WHERE c.name = 'Egyptian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Robert the Bruce', c.id, 'Robert_the_Bruce__Civ6_.png', 'Can declare a War of Liberation with Defensive Tactics, instead of Diplomatic Service. +100%  Production and +2  Movement for all units for the next 10 turns after declaring a War of Liberation.' FROM civilizations c WHERE c.name = 'Scottish';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Saladin (Sultan)', c.id, 'Saladin__Sultan___Civ6_.png', '+100% Flanking and Support bonus to all Combat and Religious units.' FROM civilizations c WHERE c.name = 'Arabian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Saladin (Vizier)', c.id, 'Saladin__Civ6_.png', 'The Worship building for Arabias Religion is 90% cheaper to purchase with  Faith. Arabian cities with that building gain +10%  Science,  Faith, and  Culture.' FROM civilizations c WHERE c.name = 'Arabian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Sejong', c.id, 'Sejong__Civ6_.png', 'Upon completing the first technology from a new era, receives  Culture equal to double the current  Science output per turn.' FROM civilizations c WHERE c.name = 'Korean';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Seondeok', c.id, 'Seondeok__Civ6_.png', 'Cities with an established Governor receive +3%  Culture and +3%  Science for each promotion that Governor has.' FROM civilizations c WHERE c.name = 'Korean';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Shaka', c.id, 'Shaka__Civ6_.png', 'May form Corps with Mercenaries, instead of Nationalism, and Armies with Nationalism, instead of Mobilization. Corps and Armies gain an additional +5  Combat Strength.' FROM civilizations c WHERE c.name = 'Zulu';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Simón Bolívar', c.id, 'Sim_n_Bol_var__Civ6_.png', 'Entering a new era grants a Comandante General, a unique type of  Great Person.' FROM civilizations c WHERE c.name = 'Gran Colombian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Suleiman (Kanuni)', c.id, 'Suleiman__Civ6_.png', 'Has access to a unique Governor, Ibrahim, the Grand Vizier. Gains the Janissary unique unit and 1  Governor title with Gunpowder.' FROM civilizations c WHERE c.name = 'Ottoman';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Suleiman (Muhteşem)', c.id, 'Suleiman__Muhteşem___Civ6_.png', '+15%  Science and  Culture when in a  Golden Age or  Heroic Age. +4  Combat Strength when not in a  Golden Age or  Heroic Age against Civilizations who are also not in a  Golden Age or  Heroic Age.' FROM civilizations c WHERE c.name = 'Ottoman';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Sundiata Keita', c.id, 'Sundiata_Keita__Civ6_.png', 'Patronage of  Great People costs 20% less  Gold. Markets gain 2 slots for  Great Works of Writing.  Great Works of Writing grant +4  Gold and +2  Production.' FROM civilizations c WHERE c.name = 'Malian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Tamar', c.id, 'Tamar__Civ6_.png', 'Gains  Faith from combat victories equal to 50% of the  Combat Strength of the defeated unit. Each  Envoy sent to a city-state of Georgias majority Religion counts as two  Envoys.' FROM civilizations c WHERE c.name = 'Georgian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Teddy Roosevelt (Bull Moose)', c.id, 'Teddy_Roosevelt__Civ6_.png', 'Tiles with Breathtaking Appeal gain +2  Science when adjacent to a Natural Wonder or Mountain, and +2  Culture when adjacent to a Wonder or Woods. +1 Appeal in all tiles in cities with a National Park.' FROM civilizations c WHERE c.name = 'American';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Teddy Roosevelt (Rough Rider)', c.id, 'Teddy_Roosevelt__Rough_Rider___Civ6_.png', '+5  Combat Strength for all units inside Americas home continent.  Envoys sent to city-states that America has a  Trade Route with count as two  Envoys. Gains the Rough Rider unique unit with Rifling.' FROM civilizations c WHERE c.name = 'American';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Teddy Roosevelt', c.id, 'Teddy_Roosevelt__Civ6_.png', '+5 Strength Combat Strength for all units inside Americas home continent. +1 Appeal to all tiles in cities with a National Park. Gains the Rough Rider unique unit with Rifling.' FROM civilizations c WHERE c.name = 'American';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Theodora', c.id, 'Theodora__Civ6_.png', 'Holy Sites provide  Culture equal to their adjacency bonus. Farms provide a +1  Faith adjacency to Hippodromes and Holy Sites.' FROM civilizations c WHERE c.name = 'Byzantine';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Tokugawa', c.id, 'Tokugawa__Civ6_.png', 'International  Trade Routes receive -25% yields and  Tourism, but domestic  Trade Routes gain +1  Culture, +1  Science, and +2  Gold for every specialty district at the destination. Cities within 6 tiles of Japans  Capital are 100% loyal and, after researching Flight, gain +1  Tourism for every specialty district.' FROM civilizations c WHERE c.name = 'Japanese';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Tomyris', c.id, 'Tomyris__Civ6_.png', '+5  Combat Strength for all units when attacking wounded units. Units heal up to 30 hit points after killing an enemy unit.' FROM civilizations c WHERE c.name = 'Scythian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Trajan', c.id, 'Trajan__Civ6_.png', 'All founded cities start with a free building in the City Center (a Monument if the game is started in the Ancient Era).' FROM civilizations c WHERE c.name = 'Roman';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Victoria (Age of Empire)', c.id, 'Victoria__Civ6_.png', 'The first city founded on each continent other than Englands home continent grants a free melee unit in that city and +1  Trade Route capacity. Building a Royal Navy Dockyard grants a free naval unit. Gains the Redcoat unique unit with Military Science.' FROM civilizations c WHERE c.name = 'English';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Victoria (Age of Steam)', c.id, 'Victoria__Age_of_Steam___Civ6_.png', '+10%  Production in cities for each Industrial Zone building in that city. +2  Production to all Strategic Resources.' FROM civilizations c WHERE c.name = 'English';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Wilfrid Laurier', c.id, 'Wilfrid_Laurier__Civ6_.png', 'Can build Farms on Tundra tiles, and on Tundra Hills tiles with Civil Engineering. Snow, Snow Hills, Tundra Hills, and Tundra tiles cost 50% less  Gold to purchase. On those tiles, resources accumulate twice as fast, Mines and Lumber Mills receive +2  Production, and Camps and Farms receive +2  Food.' FROM civilizations c WHERE c.name = 'Canadian';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Wilhelmina', c.id, 'Wilhelmina__Civ6_.png', 'Domestic  Trade Routes provide +2 Loyalty per turn for the starting city. +2  Culture for each  Trade Route sent to or received from a foreign civilization.' FROM civilizations c WHERE c.name = 'Dutch';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Wu Zetian', c.id, 'Wu_Zetian__Civ6_.png', 'Receives a free Spy with Defensive Tactics and Spies can be purchased with  Faith. Spies operate as if 1 level more experienced. Upon completing a successful offensive espionage mission on a foreign city, receives 100% of the Science,  Faith and  Culture that city produced that turn.' FROM civilizations c WHERE c.name = 'Chinese';
INSERT INTO leaders (name, civilization_id, image_key, ability) SELECT 'Yongle', c.id, 'Yongle__Civ6_.png', 'All cities gain access to three unique Lijia projects, which can either convert 50% of their  Production into  Food or  Faith, or 100% of their  Production into  Gold. Cities with 10 or more  Population gain +2  Gold, +1  Science and +1  Culture for every  Population.' FROM civilizations c WHERE c.name = 'Chinese';
